import React, { Component } from "react";

import productList from "../../productList";

//import Item from "./item";

import "./styles.css";

class ProductDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: this.props.match.params.productId,
      currentCat: []
    };
  }

  fetchProducts() {
    console.log("Product id---2" + this.state.id);

    const product = productList.filter(item => item.id == this.state.id);
    console.log(product);
    return (
      <div className="">
        <h2>{product[0].name}</h2>
        <img className="circular-image" src={product[0].images[0].src} />
        <p> {product[0].description}</p>
      </div>
    );
  }

  buttonClasses(cat) {
    let active = false;
    const { currentCat } = this.state;

    currentCat.forEach(c => {
      if (c === cat) {
        active = true;
      }
    });

    return active ? "button active" : "button";
  }

  allButtonClasses() {
    const { currentCat } = this.state;
    return currentCat.length === 0 ? "button active" : "button";
  }

  changeCategory(cat) {
    let { currentCat } = this.state;
    let found = false;

    if (cat !== "All") {
      for (let i = 0; i < currentCat.length; i += 1) {
        if (currentCat[i] === cat) {
          found = true;
          currentCat.splice(i, 1);
        }
      }

      if (!found) {
        currentCat.push(cat);
      }
    } else {
      currentCat = [];
    }

    this.setState({
      currentCat
    });
  }

  render() {
    return (
      <div className="App">
        <h1>Product Details</h1>
        <div>{this.fetchProducts()}</div>
      </div>
    );
  }
}

export default ProductDetails;
