import React, { Component } from "react";

import productList from "../../productList";

//import Item from "./item";

import "./styles.css";

class ProductDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentCat: []
    };
  }

  componentDidMount() {
    this.readProduct();
  }

  readProduct() {
    const productId = this.props.productId;
    console.log("Product id---1" + { productId });
    this.fetchProducts(productId);
  }
  fetchProducts(id) {
    console.log("Product id---2" + id);
    const product = productList.filter(product => product.id === id);

    return (
      <div className="item">
        <h2>{product.name}</h2>
        <p>{product.desc}</p>
      </div>
    );
  }

  buttonClasses(cat) {
    let active = false;
    const { currentCat } = this.state;

    currentCat.forEach(c => {
      if (c === cat) {
        active = true;
      }
    });

    return active ? "button active" : "button";
  }

  allButtonClasses() {
    const { currentCat } = this.state;
    return currentCat.length === 0 ? "button active" : "button";
  }

  changeCategory(cat) {
    let { currentCat } = this.state;
    let found = false;

    if (cat !== "All") {
      for (let i = 0; i < currentCat.length; i += 1) {
        if (currentCat[i] === cat) {
          found = true;
          currentCat.splice(i, 1);
        }
      }

      if (!found) {
        currentCat.push(cat);
      }
    } else {
      currentCat = [];
    }

    this.setState({
      currentCat
    });
  }

  render() {
    return (
      <div className="App">
        <h1>Product Details</h1>
      </div>
    );
  }
}

export default ProductDetails;
